            console.log("imdbid "+movie)
            if(movie.length<7)
                movie = '0'+movie;
            axios.get('http://www.omdbapi.com/?apikey=c262c17&i='+'tt'+movie)
        .then(res => {
            posterList.push({"imdbId":res.data.imdbID,"poster":res.data.Poster})
            if(posterList.length != movieList.length)
                console.log("getting item now")
            else
                console.log(JSON.stringify(posterList))
        })
        .catch(function (error) {
            console.log(error);
        })
        }*/

        /* after getting poster url, use below to merge 
        var arr2 = this.state.movieList
        var merged = [];
        for (var i = 0; i < arr2.length; i++) {
            var movie = arr2[i]
            var imdbId = this.parseImdbid(movie.imdbId)
            var poster = this.findPosterByImdbId(imdbId)
            arr2[i]['posterURL']=poster
        }

        console.log(JSON.stringify(arr2))*/
    }

    /*parseImdbid(imdbId) {
        var movie = ''
        if (imdbId.length < 7)
            movie = 'tt0' + imdbId;
        else
            movie = 'tt' + imdbId;
        return movie
    }

    findPosterByImdbId(imdbId) {
        var arr2 = this.state.moviePosters
        for (var i = 0; i < arr2.length; i++) {
            if (arr2[i].imdbId === imdbId)
                return arr2[i].poster
        }
    } */

    render() {
        return (

            <div>
                <Layout>
                    <Header className="header">
                        <div className="logo">
                            Movie List
                        </div>

                    </Header>
                    <Layout>
                        <Sider width={200} style={{ background: '#f0f2f5' }}>
                            <Menu
                                mode="inline"
                                defaultSelectedKeys={['1']}
                                defaultOpenKeys={['sub1']}
                                onClick={this.onMenuItemClick}
                                style={{ height: '100%', borderRight: 0 }}
                            >
                                <SubMenu
                                    key="sub1"
                                    title={
                                        <span>
                                            <Icon type="video-camera" />
                                            Years of Movies
                                        </span>
                                    }
                                >
                                    <Menu.Item key="1">{yearOptions[1]}</Menu.Item>
                                    <Menu.Item key="2">{yearOptions[2]}</Menu.Item>
                                    <Menu.Item key="3">{yearOptions[3]}</Menu.Item>
                                    <Menu.Item key="4">{yearOptions[4]}</Menu.Item>
                                    <Menu.Item key="5">{yearOptions[5]}</Menu.Item>
                                    <Menu.Item key="6">{yearOptions[6]}</Menu.Item>
                                </SubMenu>
                            </Menu>
                        </Sider>
                        <Layout style={{ padding: '0 24px 24px' }}>

                            <Breadcrumb style={{ margin: '16px 0', textAlign: 'left' }}>
                                <Breadcrumb.Item>Movies</Breadcrumb.Item>
                                <Breadcrumb.Item>{yearOptions[this.state.selectedYearKey]}</Breadcrumb.Item>
                            </Breadcrumb>
                            <Content
                                style={{
                                    background: '#f0f2f5',
                                    padding: 24,
                                    margin: 0,
                                    minHeight: 280,
                                }}
                            >

                                {/*main content */}
                                <div className='step2-1'>
                                    <div className='Genre-filters' style={{ textAlign: 'left' }}>
                                        <div style={{ fontSize: '20px', paddingBottom: '10px', fontWeight: 'bold' }}>
                                        Survey Part 3: Please select another 3 watched movies that are interesting to you, yet different from the movies you usually watch; and then provide an overall rating for each movie. 
                                        <br/>
                                        Please choose and rate movies that are different from the 15 rated movies in Part 2. 
                                        </div>

                                        <div >
                                            <Checkbox
                                                indeterminate={this.state.indeterminate}
                                                onChange={this.onCheckAllChange}
                                                checked={this.state.checkAll}
                                            >
                                                Check all genres
                                            </Checkbox>
                                        </div>
                                        <br />
                                        <div style={{ borderBottom: '1px solid grey' }}>
                                            <CheckboxGroup
                                                options={this.state.plainOptions}
                                                value={this.state.checkedList}
                                                onChange={this.onChange}
                                                style={{ marginBottom: '15px' }}
                                            />
                                        </div>
                                    </div>
                                    <div className='Movie-List'>
                                        <MovieList ref={this.childRef} genreFilters={this.state.checkedList} yearFilter={yearOptions[this.state.selectedYearKey]} fromStep3={true}></MovieList>
                                    </div>
                                </div >
                            </Content>
                        </Layout>
                    </Layout>
                </Layout>
            </div>


        );
    }
}
